import './App.css';
import axios from "axios"
import { useEffect, useState } from 'react';
import { Container } from '@material-ui/core';
import Header from './Components/header/header';
import Definition from './Components/Definitions/Definitions';

function App() {

  const [meaning, setmeaning] = useState([])
  const [language, setLanguage]=useState("en")
  const [word, setword] = useState("")

const url = `https://api.dictionaryapi.dev/api/v2/entries/${language}/${word}`
const dictionaryApi = async()=>{

  try{
     const data = await axios.get(url)
     setmeaning(data.data)
  }

  catch(error){
       console.log(error)
  }
}

console.log(meaning)

useEffect(()=>{

dictionaryApi()

},[word, language]);

  return (
    <div className="App">
      <header className="App-header" style={{backgroundColor:"#282c34", color:"white"}}>
      <Container maxWidth="md" style={{height:"100vh", display:"flex", flexDirection:'column'}}>
      <Header
       language={language}
       setLanguage={setLanguage}
       word={word}
       setword={setword}
      />
       {meaning&& <Definition meaning={meaning} language={language} word={word}/>}
      </Container>
      </header>
    </div>
  );
}

export default App;
