import React from 'react'
import "./Definitions.css"

export const Definition = ({word, meaning, language}) => {
  return (
    <div className='meanings'>
     {meaning [0] && word && language ==="en" &&(
       <audio
       src={meaning[0].phonetics[0] && meaning[0].phonetics[0].audio}
       style={{backgroundColor:"#fff", borderRadius: 10}}
       controls
       >
         Your Browser doesn't support audio element.
       </audio>
     )}
      {
          word===""?(
              <span className='subTitle'>Start by typing a word in Search</span>
          ):(
            meaning.map(mean=>mean.meaning.map(items=>items.definitions.map(def=>{
              return <div className='singleMean' style={{backgroundColor:"white", color: "black"}}>
              <b>{def.definition}</b>
              <hr style={{backgroundColor:"black"}}/>
              {def.example && (
                <span>
                  <b>Example : </b>
                  {def.example}
                </span>
              )}
              {def.synonyms &&(
                <span>
                  <b>Synonyms : </b>
                  {def.synonyms.map(syn=>`${syn},`)}
                </span>
              )}
              </div>
            })))
          )
      }
    </div>
  )
}

export default Definition;
